package Loops;

public class IfCondition {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int number=55;  
		//Check if the number is divisible by 2 or not  
		if(number%2==0){  
			System.out.println("even number");  
		}else{  
			System.out.println("odd number");  
		} 
	}

}
